import { isEmpty } from "lodash";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { ACTIVITYDATA } from "shared/constants/constants";

const ActivityData: React.FC = () => {
	const params = useParams();
    const [data, setData] = useState()
    console.log('data', data)
    useEffect(() => {
        if(params.id){
            setData(ACTIVITYDATA[params.id || 0])
        }
    },[params.id])
  return (
    <div className="mt--40 ml--20 flex flex--wrap">
        {
            !isEmpty(data) && data && 
            <>
        <div className="activity-data-card">
            <h4 className="mt--0">Steps</h4>
            <h6>{data.steps}</h6>
        </div>
        <div className="activity-data-card">
            <h4 className="mt--0">Distance</h4>
        </div>
        <div className="activity-data-card">
            <h4 className="mt--0">Heart Rate</h4>
        </div>
        <div className="activity-data-card">
            <h4 className="mt--0">Duration</h4>
        </div>
            </>
        }
    </div>
  );
};

export default ActivityData;
