import { Fragment, useCallback, useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, Filler } from 'chart.js';
import { IChartData } from '../interface/dashboard.interface';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, Filler);

interface IProps {
    userActivities: any
}

const HeartRateChart: React.FC<IProps> = (props) => {
    const { userActivities } = props;
    const [averageHeartRate, setAverageHeartRate] = useState<number>();
	const [maxHeartRateBpm, setMaxHeartRateBpm] = useState<number>();
	const handleChartData = (userActivities: any) => {
		const chartData: any = {
			labels: [],
			datasets: [
				{
					label: 'Average heart-rate',
					data: [],
					fill: true, // Fill the area under the line
					backgroundColor: '#EDFF64',
					borderColor: '#EDFF64',
					borderWidth: 0.5,
					barThickness: 6,
					borderRadius: 25
				}
			]
		};

		userActivities.forEach((item: any) => {
            console.log('item', item)
			chartData.labels.push(item.name);
			chartData.datasets[0].data.push(Math.trunc(item.heart_rate.average));
		});

		return chartData;
	};

	const chartData = handleChartData(userActivities);

	const heartRateAverage = useCallback(() => {
		let averageHeartRate = 0;
		let maxHeartRate = 0;
		userActivities.forEach((activity: any) => {
			if (activity.heart_rate) {
				const { average, max } = activity.heart_rate;
				averageHeartRate += average;
				maxHeartRate += max;
			}
		});
		setAverageHeartRate(averageHeartRate / userActivities.length);
		setMaxHeartRateBpm(maxHeartRate / userActivities.length);
	}, [userActivities]);

	useEffect(() => {
		heartRateAverage();
	}, []);

    const barOptions = {
        responsive: true,
        scales: {
            x: {
                grid: {
                    display: false
                },
                ticks: {
                    display: false
                }
            },
            y: {
                ticks: {
                    color: 'black',
                    font: {
                        weight: 'bold',
                        size: 15
                    }
                },
                grid: {
                    display: false
                }
            }
        },
        plugins: {
            legend: {
                display: false
            },
            title: {
                display: false,
                text: ''
            },
            tooltip: {
                displayColors: false
            }
        }
    };

	return (
		<Fragment>
			<p className='font-size--30 font--medium ml--30 '>
				{averageHeartRate} - {maxHeartRateBpm} <span className='font--bold'>bpm</span>
			</p>
			<Bar options={barOptions} data={chartData as IChartData} className='bar-chart--container m--30' />
		</Fragment>
	);
};

export default HeartRateChart;
