import React, { useState } from 'react';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import './MultiplePaths.css'; // Import custom CSS file for styling

const TodayaActivities = () => {
  const [tooltipVisible, setTooltipVisible] = useState(false);

  const handleMouseEnter = () => {
    setTooltipVisible(true);
  };

  const handleMouseLeave = () => {
    setTooltipVisible(false);
  };

  return (
    <div className="progress-bar-container">
      <div
        className={`tooltip ${tooltipVisible ? 'visible' : ''}`}
      >
        Progress: {70}%
      </div>
      <div className="outer-path">
        <CircularProgressbar
          value={70}
          text={`${70}%`}
          strokeWidth={10}
          styles={buildStyles({
            strokeLinecap: 'butt',
            pathColor: `rgba(62, 152, 199, 0.8)`,
            trailColor: '#d6d6d6',
          })}
        />
      </div>
      <div className="inner-path">
        <CircularProgressbar
          value={70}
          text={`${70}%`}
          strokeWidth={8}
          styles={buildStyles({
            strokeLinecap: 'butt',
            pathColor: `rgba(62, 152, 199, 0.5)`,
            trailColor: 'transparent',
          })}
        />
      </div>
    </div>
  );
};

export default TodayaActivities;
