import React, { useState } from 'react';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import './MultiplePaths.css'; // Import custom CSS file for styling

const MultiplePathsProgressBar = ({ percentage }) => {
  const [tooltipVisible, setTooltipVisible] = useState(false);

  const handleMouseEnter = () => {
    setTooltipVisible(true);
  };

  const handleMouseLeave = () => {
    setTooltipVisible(false);
  };

  return (
    <div className="progress-bar-container">
      <div
        className={`tooltip ${tooltipVisible ? 'visible' : ''}`}
      >
        Progress: {percentage}%
      </div>
      <div className="outer-path">
        <CircularProgressbar
          value={percentage}
          text={`${percentage}%`}
          strokeWidth={10}
          styles={buildStyles({
            strokeLinecap: 'butt',
            pathColor: `rgba(62, 152, 199, 0.8)`,
            trailColor: '#d6d6d6',
          })}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        />
      </div>
      <div className="inner-path">
        <CircularProgressbar
          value={percentage}
          text={`${percentage}%`}
          strokeWidth={8}
          styles={buildStyles({
            strokeLinecap: 'butt',
            pathColor: `rgba(62, 152, 199, 0.5)`,
            trailColor: 'transparent',
          })}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        />
      </div>
    </div>
  );
};

export default MultiplePathsProgressBar;
