import { useCallback, useEffect, useState } from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from "chart.js";
import { IChartData } from "../interface/dashboard.interface";
import { DAILY_STEPS, barChartOptions } from "shared/constants/constants";
import { FootIcon, HeartIcon } from "shared/components/icons/icons";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  Filler
);


const StepsChart: React.FC = (props) => {
  const [averageHeartRate, setAverageHeartRate] = useState<number>();
  const [maxHeartRateBpm, setMaxHeartRateBpm] = useState<number>();

  const data = DAILY_STEPS

  const handleChartData = useCallback((stepsData: any) => {
    console.log('stepsData', stepsData)
    const chartData: any = {
      labels: [],
      datasets: [
        {
          label: "Average heart-rate",
          data: [],
          fill: true, // Fill the area under the line
          backgroundColor: "#5792ff",
          borderColor: "#5792ff",
          borderWidth: 0.5,
          barThickness: 20,
          borderRadius: 25,
        },
      ],
    };

    stepsData.forEach((item: any) => {
      chartData.labels.push(item.day);
      chartData.datasets[0].data.push(Math.trunc(item.stepsCount));
    });

    return chartData;
  },[]);

  const chartData = handleChartData(data);

    return (
    <div className="heart-rate-chart-container">
      <div className="mb--20 flex justify-content--between align-items--center">
        <div className="flex justify-content--between align-items--center">
          <span className="heart-icon">
            <FootIcon />
          </span>
          <span className="ml--10">Heart Rate</span>
        </div>
        <p className="font-size--sm font--regular">
          {averageHeartRate} - {maxHeartRateBpm}{" "}
          <span className="font--regular">BPM</span>
        </p>
      </div>
      <Bar
        options={barChartOptions}
        data={chartData as IChartData}
        className="bar-chart__container m--30"
      />
    </div>
  );
};

export default StepsChart;
